package javapackage;

public class IfElseClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int a=13;
		int b=55;
		if(a>b)
		{
			System.out.println("The number is smaller "+a);
		}
		else
		{
			System.out.println("The number is greater "+b);
		}

	}

}
		